var test = require('tape')
var fs = require('fs')
var nativeImage = require('native-image')
var browserField = require('./browser-field')
var path = require('path')

test('should run node / electron code', function (t) {
  t.plan(7)
  t.equal(browserField(), 'from node', 'does not use browser-field')
  t.equal(process.browser, undefined, 'gets Electron process')
  t.equal(typeof fs.readFile, 'function', 'gets Electron fs')
  t.equal(path.basename(__dirname), 'fixtures', 'gets __dirname')
  t.equal(path.basename(__filename), 'test-node-with-electron.js', 'gets __filename')
  t.equal(path.basename(process.argv[2]), path.basename(__filename), 'corrected process.argv')
  t.ok(nativeImage, true, 'got electron builtin')
})
